  #Disable Azure CIS 1.3.1 checks
  curl -X PATCH -H 'Content-Type: application/json' -H 'Authorization: Bearer '$(lacework access-token) --header 'Account-name: '${lacework_subaccount} -d ${disable_cis_azure} https://${lacework_tenant}.lacework.net/api/v1/external/recommendations/azure
